package Bivio::UI::Icons;
use strict;
use Bivio::Base 'Bivio::UNIVERSAL';
use Bivio::IO::Zip;
use Bivio::MIME::Base64;

our($VERSION) = sprintf('%d.%02d', q$Revision$ =~ /\d+/g);

sub write_files {
    my($proto) = @_;
    Bivio::IO::Zip->new->read_zip_from_string(_data($proto))
	->iterate_members(sub {
	    my($name, $contents) = @_;
	    Bivio::IO::File->write($name, $contents)
		unless -e $name;
	    return 1;
	});
    return;
}

sub _data {
    my($proto) = @_;
    return \(Bivio::MIME::Base64->http_decode($proto->internal_data_section)
	|| Bivio::Die->die('decode failed'));
}

1;

